package templates

// Version: {{.Version}}
// Version Date: {{.VersionDate}}

var ProtobufTemplate = `
// Code generated by protobuf-compiler. DO NOT EDIT.
// Rerunning protobuf-comiler will overwrite this file.

syntax = "proto3";

package {{.Package}};

{{range $option := .Options}}
import "{{$option}}";
{{- end}}

{{range $i := .Imports}}
import "{{$i}}";
{{- end}}

// Services
{{range .Services}}
{{range $line := .Document}}
// {{$line}}
{{- end}}
service {{.Name}} {
	{{range $option := .Options}}
	// {{$}}
	{{- end}}

	// HTTP Client Encode
	{{range $method := .Methods}}
		{{range $doc := .Document}}
		// {{$}}
		{{- end}}
		rpc {{.Name}}  {{range $binding := $method.Bindings}}
			{{$binding.GenClientEncode}}
		{{end}}
	{{end}}
}
{{- end}}

{{range $enum := .Enums}}
{{end}}

{{range .Messages}}
message {{.Name}} {
	{{range $field := .Fields}}
		{{$field.Type}} {{$field.Name}} = {{$field.Number}};
	{{end}}
}
{{end}}
`
